import React, { useState, useLayoutEffect } from 'react';
import { StyleSheet, Text, View, SafeAreaView, FlatList, Button, Image, TouchableOpacity } from 'react-native';
import * as JogosService from '../campeonato'

export default function Home(props) {

    const {navigation} = props;

    const [camp, setCamp] = useState([])

    useLayoutEffect(() => {
        JogosService.getCampeonatos()
            .then((dados) => {
                setCamp(dados[1])
            })
            .catch((erro) => console.log(erro))
    }, [])

    return (
        <View style={{padding: 130}}>
            <TouchableOpacity
                onPress={() => navigation.navigate("Fases", camp)}
            >
                <View style={{ marginTop: 20 }}>
                    <Text style={{ fontSize: 15, color: 'red' }}>{camp.nome} </Text>
                </View>
                <Image
                style={{ height: 150, width: 150, marginTop: 20, backgroundColor: 'blue'}}
                source={{
                    uri: camp.logo,
                }}
                />

            </TouchableOpacity>
        </View>

    )
}

const styles = StyleSheet.create({});
